// Skills.jsx
import React from "react";
import { Container } from "react-bootstrap";
import Row from "react-bootstrap/Row";
import Tabs from "react-bootstrap/Tabs";
import { useScrollPosition } from "../../hooks/useScrollPosition";
import SkillsTab from "./SkillsTab";
import { Jumbotron } from "./migration";
import { SkillsData } from "../data/SkillsData"; // Import your skills data

const Skills = React.forwardRef(({ heading }, ref) => {
  const skillsTabRef = React.useRef(null);
  const [isScrolled, setIsScrolled] = React.useState(false);

  useScrollPosition(
    ({ prevPos, currPos }) => {
      if (!isScrolled && currPos.y - 400 < 0) setIsScrolled(true);
    },
    [],
    skillsTabRef
  );

  return (
    <Jumbotron ref={skillsTabRef} fluid className="bg-white m-0" id="skills">
      <Container className="p-5">
        <h2 ref={skillsTabRef} className="display-4 pb-5 text-center hao">
          {heading}
        </h2>
        <Tabs
          className={`skills-tabs ${isScrolled ? "scrolled" : ""}`}
          defaultActiveKey="hard-skills"
          id="skills-tabs"
          fill
        >
          <Row className="pt-3 px-1">
            {SkillsData.map((skill, index) => (
              <div
                key={index}
                className={`col-6 col-md-4 col-lg-3 mb-4 text-center skill-card ${
                  isScrolled ? "scrolled" : ""
                }`}
              >
                <div className="skill-icon">{skill.icon}</div>
                <p className="skill-desc">{skill.name}</p>
              </div>
            ))}
          </Row>
        </Tabs>
      </Container>
    </Jumbotron>
  );
});

export default Skills;
